.chip {
  display: flex;
  width: fit-content;
  padding: 0 25px;
  height: 50px;
  font-size: 16px;
  line-height: 50px;
  border-radius: 25px;
  background-color: rgba(255, 255, 0, 0.477); /* Giallo chiaro */
  margin-bottom: 10px; /* Distanza tra le chip */
}

.chip .star-icon {
  font-size: 24px; /* Dimensione dell'icona della stella */
  color: #ffd700; /* Colore della stella (oro) */
  margin-right: 10px; /* Spazio tra l'icona e il testo */
}

.chip .views-icon {
  font-size: 24px; /* Icona degli occhi */
  margin-right: 10px;
}

.chip .views-text {
  font-size: 16px; /* Font size per le visualizzazioni */
  color: lightblue; /* Colore light blue */
}



body {
  font-family: Arial, sans-serif;
  margin: 0;
  padding: 0;
}

.d-flex {
  padding: 20px;
  display: flex;
  justify-content: space-between; /* Aggiunge spazio tra il testo e l'immagine */
  align-items: flex-start; /* Allinea il contenuto in alto */
}

.d-flex .text-container {
  display: flex;
  flex-direction: column; /* Impila i testi uno sotto l'altro */
}

.d-flex .h1 {
  font-size: 3rem;
  margin-bottom: 0.5rem;
  color: #333; /* Colore del testo */
}

.d-flex .h3 {
  font-size: 1.8rem;
  margin-bottom: 0;
  color: #666; /* Colore del testo */
}

.d-flex .image-container img {
  height: 300px !important;
  border-radius: 8px; /* Angoli arrotondati per l'immagine */
  object-fit: cover; /* Rende l'immagine proporzionata senza distorsioni */
  margin-left: 20px; /* Spazio tra il testo e l'immagine */
}

.audio-container {
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    gap: 20px;
    padding: 10px;
}

.audio-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
}

.icon {
    font-size: 24px;
    margin-bottom: 8px;
}

.audio-player {
    border-radius: 8px;
    width: 100%;
    max-width: 200px;
}

body {
  font-family: Arial, sans-serif;
  background-color: #f4f4f4;
  margin: 0;
  padding: 0;
}

.song-cards-container {
  display: flex;
  flex-direction: column;  /* Disporre le card in colonna */
  width: 100%;             /* Le card prenderanno tutta la larghezza disponibile */
  align-items: stretch;    /* Assicurarsi che le card si allarghino per occupare tutta la larghezza */
  padding: 20px;
}

.song-card {
  width: 100%;  /* Ogni card occuperà il 100% della larghezza disponibile */
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  margin-bottom: 20px;  /* Spazio tra le card */
  transition: transform 0.3s ease;
}

.song-card:hover {
  transform: translateY(-10px); /* Effetto di sollevamento al passaggio del mouse */
}

.card-content {
  padding: 20px;
}

.song-title {
  font-size: 1.6rem;
  color: #333;
  margin-bottom: 10px;
}

.song-artists, .song-album {
  font-size: 1.1rem;
  color: #666;
  margin: 5px 0;
}

.song-artists {
  font-style: italic;
}

.song-album {
  color: #888;
}
